import {useState} from 'react';

const Home = () => {
    const[blogs, setBlogs]=useState([
        {title:'My first React App', body:'lorem ipsum...', author:'Josh', id:1},
        {title:'Workout Tips', body:'lorem ipsum...', author:'Marty', id:2},
        {title:'Healthy Diet', body:'lorem ipsum...', author:'Pam', id:3}
    ]);


    return (
        <div className="home">
            {blogs.map((blog) => (
                <div className="blog-preview" key={blog.id}>
                    <h2>{blog.title}</h2>
                    <p>Written by {blog.author}</p>
                </div>
            ))}
        </div>
    );
}

export default Home;
